                // Obliczanie CRC-16/XMODEM 
                var crcResult = calculateCRC16XMODEM(msg.buffer, 8, msg.buffer.byteLength-3);
                var crcorg = (msg.buffer[msg.buffer.byteLength-2] << 8) | msg.buffer[msg.buffer.byteLength-1]
                var drugiCRC = {
                    "org": crcorg,
