                }

                msg.drugiCRC = drugiCRC;  
                return msg;
            }
        }
    }
};

/**
* @param {string | any[]} buffer
* @param {number} startByte
* @param {number} endByte
*/
function calculateCRC16XMODEM(buffer, startByte, endByte) {
    if (startByte < 0 || endByte < startByte || endByte >= buffer.length) {
        console.error("Nieprawidłowe parametry wejściowe.");
        return null;
    }

    let crc = 0x0000;

    for (let i = startByte; i <= endByte; i++) {
        crc ^= (buffer[i] << 8);

        for (let j = 0; j < 8; j++) {
            if (crc & 0x8000) {
                crc = (crc << 1) ^ 0x1021;
            } else {
                crc <<= 1;
            }
        }
    }

    return crc & 0xFFFF;
}

