var local = flow.get('listaParametrow');


// Zakładamy, że msg.objectResults zawiera zmienne z nazwami parametrów.
let msgPayload = msg.objectResults;

// Iterujemy przez wszystkie właściwości w msg.objectResults
for (let propertyName in msgPayload) {
    if (msgPayload.hasOwnProperty(propertyName)) {
        // Sprawdzamy, czy nazwa zmiennej znajduje się w local i czy jest obiektem
        if (local.hasOwnProperty(propertyName) && typeof local[propertyName] === 'object') {
            // Przypisujemy wartość zmiennej do odpowiedniego obiektu w local
            // Sprawdzimy czy jest scalowanie wartości 
            if (msgPayload[propertyName].scale == "1") {
                local[propertyName].value = parseInt(msgPayload[propertyName].value);
            } else {
                //z zaokrągleniem do jednego miejsca po przecinku jak jest skalowanie
                local[propertyName].value = parseFloat(msgPayload[propertyName].value).toFixed(1);
            }

        }
    }
}

