var newMsg = { payload:""};

var local = flow.get('komunikacja');
if (local == undefined) {
    local = {
        "F_1": false,
        "F_2": false,
        "F_3": false,
        "F_4": false,
        "F_5": false,
        "F_6": false,
        "F_7": false,
        "F_8": false,
        "F_9": false,
        "F_10": false,
        "F_11": false,
        "F_12": false,
        "F_13": false,
        "F_14": false
    };
};

//Sprawdzenie komunikacji
var statusKomunikacji;

// Sprawdzanie czy wszystkie wartości w zmiennej 'local' są true
var allTrue = Object.values(local).every(function (value) {
    return value === true;
});

// Ustawianie zmiennej 'statusKomunikacji' na podstawie wyniku
if (allTrue) {
    statusKomunikacji = "ok";
} else {
    var falseVariables = Object.keys(local).filter(function (key) {
        return local[key] === false;
    });

    if (falseVariables.length === 1) {
        statusKomunikacji = falseVariables[0];
    } else {
        statusKomunikacji = falseVariables.join(", ");
    }
}

// Ustawianie wszystkich wartości w 'local' na false
for (var key in local) {
    local[key] = false;
}

//ustawienie wyjścia
newMsg.payload = statusKomunikacji;

//zapisa do pamięci
flow.set('statusKomunikacji', statusKomunikacji);

//Zapis do pamieci
flow.set('komunikacja', local);

return newMsg;

