/**
* @param {string | any[]} buffer
* @param {number} numBytes
*/
function calculateCRC8dvbs2(buffer, numBytes) {
    /**
* @param {number} crc
* @param {number} a
*/
    function crc8_dvb_s2(crc, a) {
        crc ^= a;
        for (let ii = 0; ii < 8; ++ii) {
            if (crc & 0x80) {
                crc = (crc << 1) ^ 0xD5;
            } else {
                crc = crc << 1;
            }
        }
        return crc & 0xFF; // Maskowanie do 8 bitów
    }

    if (numBytes > buffer.length) {
        console.error("Liczba bajtów do obliczenia CRC przekracza długość bufora.");
        return null;
    }

    let crcResult = 0;

    for (let i = 0; i < numBytes; i++) {
        crcResult = crc8_dvb_s2(crcResult, buffer[i]);
    }

    return crcResult;
}

