/**
* @param {string | any[]} buffer
* @param {number} numBytes
*/
function calculateCRC8dvbs2(buffer, numBytes) {
    /**
* @param {number} crc
* @param {number} a
*/
    function crc8_dvb_s2(crc, a) {
        crc ^= a;
        for (let ii = 0; ii < 8; ++ii) {
            if (crc & 0x80) {
                crc = (crc << 1) ^ 0xD5;
            } else {
                crc = crc << 1;
            }
        }
        return crc & 0xFF; // Maskowanie do 8 bitów
    }

    if (numBytes > buffer.length) {
        console.error("Liczba bajtów do obliczenia CRC przekracza długość bufora.");
        return null;
    }

    let crcResult = 0;

    for (let i = 0; i < numBytes; i++) {
        crcResult = crc8_dvb_s2(crcResult, buffer[i]);
    }

    return crcResult;
}

/**
* @param {string | any[]} buffer
* @param {number} startByte
* @param {number} endByte
*/
function calculateCRC16XMODEM(buffer, startByte, endByte) {
    if (startByte < 0 || endByte < startByte || endByte >= buffer.length) {
        console.error("Nieprawidłowe parametry wejściowe.");
        return null;
    }

    let crc = 0x0000;

    for (let i = startByte; i <= endByte; i++) {
        crc ^= (buffer[i] << 8);

        for (let j = 0; j < 8; j++) {
            if (crc & 0x8000) {
                crc = (crc << 1) ^ 0x1021;
            } else {
                crc <<= 1;
            }
        }
    }

    return crc & 0xFFFF;
}

